\begin{theorem}
Let $\mathcal M$ denote the Mellin transform. For a non-negative integer $n \in \mathbb{N}_{\ge 0}$ and a function $f(t)$, if both $\mathcal M \left\{ f \left(t\right) \right\} \left(s\right)$ and $\mathcal M \left\{ t^n f \left(t\right) \right\} \left(s\right)$ exist, then
\[
\mathcal M \left\{ t^n f \left(t\right) \right\} \left(s\right) = \mathcal M \left\{ f \left(t\right) \right\} \left(s + n\right).
\]
\end{theorem}

\begin{theorem}[Euclid's Elements, Book V, Proposition 17 (Separando)]
If four magnitudes are proportional, then the excess of the first over the second is to the second as the excess of the third over the fourth is to the fourth. That is: $a:b = c:d \implies (a-b):b = (c-d):d$.
\end{theorem}

Prove that for any ordinal $x$, the following properties of ordinal multiplication hold, where $1$ is defined as the successor of the empty set:
\begin{align*}
x \cdot 1 &= x \\
1 \cdot x &= x
\end{align*}

Let $(S, \preceq)$ be an ordered set, and let $(S, \succeq)$ be its dual ordered set. The following statements are equivalent:
\begin{enumerate}
    \item $\Sigma$ is true for $(S, \preceq)$.
    \item $\Sigma^*$ is true for $(S, \succeq)$.
\end{enumerate}

\begin{theorem}
These sets of logical connectives are not functionally complete.
\end{theorem}

\begin{theorem}
Let $(M_1, d_1)$ and $(M_2, d_2)$ be metric spaces. If $g: M_1 \to M_2$ is Lipschitz continuous, then $g$ is uniformly continuous on $M_1$.
\end{theorem}

\begin{theorem}
Let $\omega$ denote the set of natural numbers (including zero). If a subset $S \subseteq \omega$ satisfies the following two conditions:
\begin{enumerate}
    \item $0 \in S$, and
    \item for every $x \in S$, its successor $x^+$ is also in $S$,
\end{enumerate}
then $S = \omega$.
\end{theorem}

\begin{theorem}
For any positive integer $n$, the following identity holds:
$$ \sum_{j=1}^\infty \frac{1}{(2j-1)^{2n}} = \frac{1}{1^{2n}} + \frac{1}{3^{2n}} + \frac{1}{5^{2n}} + \cdots = (-1)^{n+1} \frac{B_{2n}(2^{2n}-1)\pi^{2n}}{2(2n)!} $$
\end{theorem}

\begin{theorem}
Every particular point space is scattered.
\end{theorem}

\begin{theorem}
Let $T_A$ and $T_B$ be topological spaces, and let $\phi: T_A \to T_B$ be a closed bijection. If $T_A$ is a $T_{2 \frac 1 2}$ (completely Hausdorff) space, then $T_B$ is also a $T_{2 \frac 1 2}$ (completely Hausdorff) space.
\end{theorem}

The following numerical identity holds:
\begin{equation*}
159 = 14 \cdot 1^4 + 4 \cdot 2^4 + 3^4
\end{equation*}

\begin{theorem}
The \text{quaternion group} $Q$ is not isomorphic to the \text{dihedral group} $D_4$.
\end{theorem}

Let $A_{n-2}$ be a subgroup of the alternating group $A_n$ on $n$ letters. For any element $x \in A_{n-2}$ that can be expressed as a product of transpositions $x = x_1 x_2 \dots x_k$, where each $x_i$ is a transposition from a given set $H$, prove that there exists an element $y \in A_n$, expressible as a product of transpositions $y = y_1 y_2 \dots y_j$, such that:
\begin{enumerate}
    \item $yx = z$, where $z$ contains no transpositions from $H$; and
    \item $y_a \ne x_b$ for all $a, b$.
\end{enumerate}

