Let $G$ be an $n$-dimensional vector space and $H$ be a vector space. Let $\phi: G \to H$ be a linear transformation.
Then the image of $\phi$ is finite-dimensional.
The rank-nullity theorem states that the sum of the rank of $\phi$, denoted $\rho(\phi)$, and the nullity of $\phi$, denoted $\nu(\phi)$, equals the dimension of $G$. That is,
$$ \rho(\phi) + \nu(\phi) = n. $$
Equivalently, this can be expressed as the sum of the dimension of the image of $\phi$ and the dimension of the kernel of $\phi$ equaling the dimension of $G$:
$$ \dim(\operatorname{Im}(\phi)) + \dim(\Ker(\phi)) = \dim(G). $$
This theorem is also applicable to matrices.

\end{document}

\begin{theorem}
Let $S$ be a set and $\circ$ be an associative binary operation on $S$. For any positive integer $n$, let $a_1, \ldots, a_n$ be elements of $S$. Define the set $P_n(a_1, \ldots, a_n)$ inductively as follows:
\begin{itemize}
    \item $P_1(a_1) = \{a_1\}$;
    \item $P_2(a_1, a_2) = \{a_1 \circ a_2\}$;
    \item For $n > 2$, $P_n(a_1, \ldots, a_n) = \{x \circ y \mid x \in P_r(a_1, \ldots, a_r) \text{ and } y \in P_s(a_{r+1}, \ldots, a_{r+s}) \text{ for some positive integers } r, s \text{ such that } r+s=n\}$.
\end{itemize}
Then the set $P_n(a_1, \ldots, a_n)$ contains exactly one element, which is denoted by $a_1 \circ a_2 \circ \ldots \circ a_n$.
\end{theorem}

\begin{theorem}
A subset of a countable set is countable.
\end{theorem}

\begin{theorem}
Let $(S, \circ)$ be a monoid with identity element $e_S$. Let $C$ be the set of all cancellable elements of $S$, and let $T$ be the set of all invertible elements of $S$. Then $(T, \circ)$ is a subgroup of $(C, \circ)$.
\end{theorem}

\begin{theorem}
Let $x$ be a limit ordinal and $y$ be a non-zero ordinal. If $\langle a_i \rangle_{i=1}^n$ is a strictly decreasing sequence of ordinals and $\langle b_i \rangle_{i=1}^n$ is a sequence of finite ordinals, then
$$ \left(\sum_{i=1}^n x^{a_i} b_i\right) \cdot x^y = x^{a_1 + y} $$
\end{theorem}

\begin{theorem}
Let $(X, \|\cdot\|)$ be a normed vector space. For any open ball $B_\epsilon(x)$ in $(X, \|\cdot\|)$ (where $x \in X$ and $\epsilon > 0$), and for any point $y \in B_\epsilon(x)$, there exists a $\delta > 0$ such that the open ball $B_\delta(y)$ is a subset of $B_\epsilon(x)$.
\end{theorem}

\begin{theorem}
For real constants $a$ and $p$ such that $a^2+p^2 \neq 0$, the indefinite integral of $\sinh ax \cos px$ with respect to $x$ is given by
$$ \int \sinh ax \cos px \, \mathrm{d}x = \frac {a \cosh ax \cos px + p \sinh ax \sin px} {a^2 + p^2} + C $$
where $C$ is the constant of integration.
\end{theorem}

\begin{theorem}
Let $\mathcal L$ be a straight line defined by the equation $ax - by = c$. The shortest distance $d$ between any two distinct lattice points on $\mathcal L$ is given by $d = \frac{\sqrt{a^2 + b^2}}{\gcd(a, b)}$, where $\gcd(a, b)$ denotes the greatest common divisor of $a$ and $b$.
\end{theorem}

\begin{theorem}
Let $n$ be an integer. If $\sqrt{n}$ is a rational number, then $\sqrt{n}$ is an integer.
\end{theorem}

\begin{theorem}
Let $\mathcal R$ be a partial order on a set $S$. Then its reflexive reduction $\mathcal R^\ne$ is a strict partial order on $S$.
\end{theorem}

Let $f(x)$ be a real-valued function defined on $[0, 2\pi]$ by
\[
f(x) = \begin{cases}
\sin\left(\frac{x}{2}\right) & : 0 \le x < \pi \\
-\sin\left(\frac{x}{2}\right) & : \pi < x \le 2 \pi
\end{cases}
\]
Then its Fourier series is given by
\begin{align*}
f(x) &\sim \frac{8}{\pi} \sum_{n=1}^\infty (-1)^{n-1} \frac{n \sin(nx)}{4n^2 - 1} \\
&= \frac{8}{\pi} \left( \frac{\sin x}{1 \times 3} - \frac{2 \sin(2x)}{3 \times 5} + \frac{3 \sin(3x)}{5 \times 7} - \frac{4 \sin(4x)}{7 \times 9} + \frac{5 \sin(5x)}{9 \times 11} - \dotsb \right)
\end{align*}

Let $X$ be a compact metric space and $(f_n)_{n=1}^\infty$ be a sequence of continuous functions from $X$ to $\C$. If the series $\sum_{n=1}^\infty f_n$ converges uniformly absolutely on $X$, then:
\begin{enumerate}
    \item The infinite product $f(x) = \prod_{n=1}^\infty (1 + f_n(x))$ converges uniformly absolutely on $X$.
    \item The function $f$ is continuous on $X$.
    \item There exists an integer $n_0 \in \N$ such that the product $\prod_{n=n_0}^\infty (1 + f_n(x))$ has no zeroes for any $x \in X$.
\end{enumerate}

Let $\lambda \in \mathbb{R}_{>0}$ be a strictly positive real number. The Fourier series of the absolute value function $f(x) = |x|$ on the open real interval $(-\lambda, \lambda)$ is given by:
\begin{align*}
f(x) &\sim \frac{\lambda}{2} - \frac{4\lambda}{\pi^2} \sum_{n=0}^\infty \frac{1}{(2n+1)^2} \cos\left(\frac{(2n+1)\pi x}{\lambda}\right) \\
&= \frac{\lambda}{2} - \frac{4\lambda}{\pi^2} \left(\cos\left(\frac{\pi x}{\lambda}\right) + \frac{1}{3^2} \cos\left(\frac{3\pi x}{\lambda}\right) + \frac{1}{5^2} \cos\left(\frac{5\pi x}{\lambda}\right) + \dotsb\right)
\end{align*}
